import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import ProductList from "./ProductDetails";
import ProductDetails from "./ProductDetails";
import Cart from "./Cart";

const productsData = [
  { id: 1, name: "Product 1", price: 20, image: "product1.jpg" },
  { id: 2, name: "Product 2", price: 30, image: "product2.jpg" },
  { id: 3, name: "Product 3", price: 25, image: "product3.jpg" },
];

const App = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  return (
    <BrowserRouter>

      <div className="App">
      <Routes>
          <Route exact path="/" render={() => <ProductList products={productsData} onAddToCart={addToCart} />} />
          <Route
            path="/products/:id"
            render={(props) => <ProductDetails {...props} products={productsData} onAddToCart={addToCart} />}
          />
          <Route path="/cart" render={() => <Cart cart={cart} />} />
          </Routes>
      </div>

    </BrowserRouter>
  );
};

export default App;
